import React from "react";

import styles from "./TodoItem.module.css";

const TodoItem: React.FC<{
  id: string;
  text: string;
  onRemove: (id: string) => void;
}> = (props) => {
  const onRemoveHandler = (e: React.MouseEvent) => { // defining the type of event is optional here cause the function is not using the event.
    props.onRemove(props.id);
  };

  return (
    <li className={styles.item} onClick={onRemoveHandler}>
      {props.text}
    </li>
  );
};

export default TodoItem;
